mapper.update:
update c_competition_record
enum_id = #{enumId}, cc_r_time = #{ccRTime}, cc_r_speci = #{ccRSpeci}, cc_r_name = #{ccRName}, cc_r_audit = #{ccRAudit}, cr_id = #{crId},
where cc_r_id = #{ccRId}

UPDATE c_personnel_sheet cps
	INNER JOIN c_ballteam cb ON cps.bal_id = cb.bal_id
SET ps_audit = #{psAudti}
WHERE cps.stu_id = #{stuId}
  AND cb.cc_r_id = #{ccrId}
