mapper.select:


     and et_id = #{etId}
     and stu_id = #{stuId}
     and tea_id = #{teaId}
     and ms_time = #{msTime}
     and ms_dribble = #{msDribble}
     and ms_shooting = #{msShooting}
     and ms_score = #{msScore}
     and ms_1 = #{ms1}
     and ms_2 = #{ms2}
     and ms_3 = #{ms3}
     and ms_class = #{msClass}
     and ms_img = #{msImg}

order by ms_time asc

SELECT
SUM(ms.ms_score)/COUNT(*)
FROM class_register as cr
JOIN a_exercise_task as et on cr.cr_id=et.cr_id
JOIN a_mark_sheet as ms on et.et_id=ms.et_id
WHERE et.enum_id=2

    AND cr.enum_id=#{enumId}


    AND cr.semester_id=#{semesterId}


    AND cr.class_id = #{classId}


    AND ms.ms_time < #{msTime}

mapper.update:
update a_mark_sheet
et_id = #{etId}, stu_id = #{stuId}, tea_id = #{teaId}, ms_time = #{msTime}, ms_dribble = #{msDribble}, ms_shooting = #{msShooting}, ms_score = #{msScore}, ms_1 = #{ms1}, ms_2 = #{ms2}, ms_3 = #{ms3}, ms_class = #{msClass}, ms_img = #{msImg},
where ms_id = #{msId}
